import { observable } from 'mobx';

class CounterStore {

    // observable로 덮어주면, 이 count는 특정 observer가 바라보는 state 객체가 되는것 
    @observable
    _count = 5

    increment() {
        this._count++;
    }
    decrement() {
        this._count--;
    }
}

export default new CounterStore();
