import { observable, action, computed, makeObservable } from 'mobx';

class TodoStore {

    constructor() {
        makeObservable(this);
    }

    @observable
    _todo = {

    } // id, title, date

    @observable
    _todos = [];

    get todos() {
        return this._todos;
    }

    get todo() {    // get 메소드 사용 
        return this._todo;
    }

    @action // observable 데이터를 변경할때에는 @action 데코레이터 사용
    setTodoProps(name, value) {
        this._todo = {
            ...this._todo,
            [name]: value
        }
    }
}


export default new TodoStore();
